IF OBJECT_ID ('rally.DAKAR') is not null drop table rally.DAKAR;CREATE TABLE rally.DAKAR (ID TINYINT IDENTITY,COMAND VARCHAR(10),TIME TIME);;



select *,LAG (TIME,1,'00:00:00') OVER (PARTITION BY COMAND ORDER BY TIME) as PREV_TIME,DATEDIFF(s, TIME, (LAG (TIME,1,'00:00:00') OVER (PARTITION BY COMAND ORDER BY TIME))) as SALDO,DATEDIFF(s, TIME, (LAG (TIME,1,'00:00:00') OVER (PARTITION BY COMAND ORDER BY TIME)))/60 as SALDO60,DATEDIFF(mi, TIME, (LAG (TIME,1,'00:00:00') OVER (PARTITION BY COMAND ORDER BY TIME))) as SALDOMI,FIRST_VALUE (TIME) OVER (PARTITION BY COMAND ORDER BY TIME) as BEST_TIME,LAST_VALUE (TIME) OVER (ORDER BY COMAND ) as FAIL_TIMEfrom rally.DAKAR